package org.mortbay.jetty.cert;import java.net.MalformedURLException;import org.eclipse.jetty.server.handler.ResourceHandler;import org.eclipse.jetty.util.StringUtil;import org.eclipse.jetty.util.URIUtil;import org.eclipse.jetty.util.resource.Resource;/** * Security patch for the resource handler. * @author mhald */public class JettyResourceHandler extends ResourceHandler {	public Resource getResource(String path) throws MalformedURLException {		if (path != null && path.indexOf('?') >= 0)		{			path = URIUtil.decodePath(URIUtil.canonicalPath(StringUtil.replace(path, "?", "%3F")));			if (path == null)				return null;		}				return super.getResource(path);	}}